package se.nrm.mediaserver.mediaserverclient;

import java.io.IOException;
import java.io.UnsupportedEncodingException;
import java.net.HttpURLConnection;
import java.util.logging.Level;
import java.util.logging.Logger;
import org.apache.http.HttpEntity;
import org.apache.http.HttpResponse;
import org.apache.http.client.HttpClient;
import org.apache.http.client.methods.HttpPost;
import org.apache.http.entity.ContentType;
import org.apache.http.entity.StringEntity;
import org.apache.http.impl.client.HttpClientBuilder;

/**
 *
 * @author ingimar
 */
public class Main {

    public static void main(String[] args) {

        HttpClient client = HttpClientBuilder.create().build();
        HttpResponse response = null;

        try {

            String myJSON = "{\"owner\":\"ida-li\",\"access\":\"public\",\"licenseType\":\"CC BY\",\"legend\":\"this is an image\",\"fileName\":\"image.jpg\",\n"
                    + "\"fileDataBase64\":\"\"}";
//            StringEntity entity = new StringEntity(myJSON, "application/json", "UTF-8");
            StringEntity entity = new StringEntity(myJSON, ContentType.APPLICATION_JSON);
            
            String url = "http://127.0.0.1:8080/MediaServerResteasy/media";
            HttpPost post = new HttpPost(url);
            post.setEntity(entity);
            response = client.execute(post);
            HttpEntity e = response.getEntity();

        } catch (UnsupportedEncodingException ex) {
            Logger.getLogger(Main.class.getName()).log(Level.SEVERE, null, ex);
        } catch (IOException ex) {
            Logger.getLogger(Main.class.getName()).log(Level.SEVERE, null, ex);
        }
        System.out.println("Response is " + response);
    } // end main
}
